package algorithm.easy;import java.util.HashSet;import java.util.Set;public class FairCandySwap {    public int[] fairCandySwap(int[] A, int[] B) {        int sumA = 0;        for (int a : A) {            sumA += a;        }        int sumB = 0;        Set<Integer> setB = new HashSet();        for (int b : B) {            sumB += b;            setB.add(b);        }        int dif = Math.abs(sumA - sumB) / 2;        int[] res = new int[2];        for (int a : A) {            if (sumA > sumB) {                if (setB.contains(a - dif)) {                    res[0] = a;                    res[1] = a - dif;                    break;                }            } else {                if (setB.contains(a + dif)) {                    res[0] = a;                    res[1] = a + dif;                    break;                }            }        }        return res;    }}