package algorithm.easy;public class FlippingAnImage {    public int[][] flipAndInvertImage(int[][] A) {        for (int i = 0; i < A.length; i++) {            RIArr(A[i]);        }        return A;    }    private void RIArr(int[] A) {        int len = A.length;        for (int i = 0; i < len / 2; i++) {            if (A[i] == A[len - 1 - i]) {                invert(A, i);                invert(A, len - 1 - i);            }        }        if (len % 2 != 0) {            invert(A, len / 2);        }    }    private void invert(int[] A, int index) {        if (A[index] == 0) A[index] = 1;        else A[index] = 0;    }    private void swap(int[] arr, int i, int j) {        int temp = arr[i];        arr[i] = arr[j];        arr[j] = temp;    }}