package algorithm.easyclass JewelsAndStones {    fun numJewelsInStones(J: String, S: String): Int {        if (J.isEmpty() || S.isEmpty()) return 0//        val jewelSet = HashSet<Char>()//        jewelSet.addAll(J.toCharArray().asList())        var count = 0        for (s in S){            if(J.indexOf(s) >= 0)                count++        }        return count//        return S.toCharArray().count { J.indexOf(it) >= 0 }    }}