package algorithm.easy;import java.util.PriorityQueue;public class KthLargest {    private PriorityQueue<Integer> q;    private int k;    public KthLargest(int k, int[] nums) {        this.k = k;        q = new PriorityQueue<>(k);        for (int i : nums) q.add(i);    }    public int add(int val) {        if (q.size() < k) {            q.offer(val);        } else if (q.peek() < val) {            q.poll();            q.offer(val);        }        return q.peek();    }}