package algorithm.easy;import java.util.PriorityQueue;import java.util.Queue;public class MaxSumAfterKNegation {    public int largestSumAfterKNegations(int[] A, int K) {        Queue<Integer> queue = new PriorityQueue<>(K);        for(int i:A) queue.add(i);        while(K-->0) queue.add(-queue.poll());        int sum=0;        for(int i=0;i<A.length;i++) sum+=queue.poll();        return sum;    }}