package algorithm.easy;public class MyHashSet {    private int buckets = 1000;    private boolean[][] arr = new boolean[buckets][];    /**     * Initialize your data structure here.     */    public MyHashSet() {    }    private int pos(int val) {        return val / buckets;    }    private int hash(int val) {        return val % buckets;    }    public void add(int key) {        boolean[] itemBucket = arr[pos(key)];        int itemPerBuckets = 1001;        if (itemBucket == null)            arr[pos(key)] = new boolean[itemPerBuckets];        arr[pos(key)][hash(key)] = true;    }    public void remove(int key) {        boolean[] itemBucket = arr[pos(key)];        if (itemBucket != null) {            arr[pos(key)][hash(key)] = false;        }    }    /**     * Returns true if this set contains the specified element     */    public boolean contains(int key) {        boolean[] itemBucket = arr[pos(key)];        return itemBucket != null && arr[pos(key)][hash(key)];    }}