package algorithm.easy;import java.util.Arrays;public class ShortestCompletingString {    public String shortestCompletingWord(String P, String[] words) {        int[] counts = new int[26];        for (char c : P.toLowerCase().toCharArray()) {            if (!Character.isLetter(c)) continue;            counts[c - 'a']++;        }        int min = Integer.MAX_VALUE;        String res = "";        int[] wCounts = new int[26];        for (String w : words) {            if (w.length() > min) continue;            Arrays.fill(wCounts, 0);            for (char c : w.toLowerCase().toCharArray()) {                if (!Character.isLetter(c)) continue;                wCounts[c - 'a']++;            }            if (same(counts, wCounts)) {                res = w;                min = Math.min(min, w.length());            }        }        return res;    }    private boolean same(int[] a, int[] b) {        for (int i = 0; i < a.length; i++) if (a[i] != 0 && a[i] != b[i]) return false;        return true;    }}