package algorithm.easy;public class RotateString {    public boolean rotateString(String A, String B) {        if (A.length() != B.length()) return false;        if (A.length() == 0) return true;        int len = A.length();        for (int j = 0; j < len; j++) {            while (j < len && A.charAt(0) != B.charAt(j)) {                j++;            }            if (B.substring(j, len).equals(A.substring(0, len - j)))                return B.substring(0, j).equals(A.substring(len - j, len));        }        return false;    }}