package algorithm.easy;public class SortArrayByParity {    public int[] sortArrayByParity(int[] A) {        int j = 1;        for (int i = 0; i < A.length; i++) {            if (isEven(A[i])) continue;            for (j= Math.max(j, i); j < A.length; j++) {                if (isEven(A[j])) {                    swap(A, i, j);                    break;                }            }        }        return A;    }    private boolean isEven(int a) {        return a % 2 == 0;    }    private boolean isOdd(int a) {        return a % 2 != 0;    }    private void swap(int[] arr, int i, int j) {        int temp = arr[i];        arr[i] = arr[j];        arr[j] = temp;    }}