package algorithm.easy;import java.util.ArrayList;import java.util.HashMap;import java.util.List;public class SubdomainVisitCount {    public List<String> subdomainVisits(String[] cpdomains) {        HashMap<String, Integer> map = new HashMap<>();        for (String cpd : cpdomains) {            int index = cpd.indexOf(' ');            int count = Integer.parseInt(cpd.substring(0, index));            String domain = cpd.substring(index + 1);            map.put(domain, map.getOrDefault(domain, 0) + count);            for (int i = 0; i < domain.length(); i++) {                if (domain.charAt(i) == '.') {                    String dom = domain.substring(i + 1);                    map.put(dom, map.getOrDefault(dom, 0) + count);                }            }        }        return listMap(map);    }    private List<String> listMap(HashMap<String, Integer> map) {        List<String> res = new ArrayList<>(map.size());        for (String key : map.keySet()) {            res.add(map.get(key) + " " + key);        }        return res;    }}