package algorithm.easy;public class ToLowerCase {    public String toLowerCase(String str) {        if (str == null || str.length() == 0) return str;        char[] arr = str.toCharArray();        int alter = 'A' - 'a';        for (int i = 0, len = arr.length; i < len; i++) {            if (arr[i] >= 'A' && arr[i] <= 'Z') arr[i] = (char) (arr[i] - alter);        }        return String.valueOf(arr);    }}