package algorithm.easy;import java.util.HashSet;public class UniqueEmailAddress {    public int numUniqueEmails(String[] emails) {        if (emails == null || emails.length == 0) return 0;        HashSet<String> uniqueEmails = new HashSet<>();        for (String email : emails) {            int index = email.indexOf('@');            String prefix = shrinkPrefix(email.substring(0, index - 1));            String suffix = email.substring(index, email.length());            if (uniqueEmails.contains(prefix + suffix)) continue;            uniqueEmails.add(prefix + suffix);        }        return uniqueEmails.size();    }    private String shrinkPrefix(String prefix) {        prefix = prefix.replace(".", "");        int index = prefix.indexOf("+");        if (index < 0) return prefix;        return prefix.substring(0, index - 1);    }}