package algorithm.easy;import java.util.HashSet;public class UniqueMorseRepresentation {    public int uniqueMorseRepresentations(String[] words) {        String[] morse = {                ".-", "-...", "-.-.", "-..", ".", "..-.", "--.", "....", "..", ".---", "-.-", ".-..", "--", "-.", "---", ".--.", "--.-", ".-.", "...", "-", "..-", "...-", ".--", "-..-", "-.--", "--.."        };        HashSet<String> moresRep = new HashSet<>();        StringBuilder builder = new StringBuilder();        for (String word : words) {            builder.setLength(0);            for (char c : word.toCharArray()) {                builder.append(morse[c - 'a']);            }            if (moresRep.contains(builder.toString())) continue;            moresRep.add(builder.toString());        }        return moresRep.size();    }}