package algorithm.foundation.sortimport algorithm.base.BaseTestImplclass InsertionSort : BaseTestImpl() {    override fun sort(a: Array<Comparable<*>>) {        val N = a.size        for (i in 1 until N) {            var j = i            while (j > 0 && less(a[j], a[j - 1])) {                print("before exch: ")                sop(*a)                exch(a, j, j - 1)                print("after exch : ")                sop(*a)                println()                j--            }        }    }}