package algorithm.foundation.sortimport algorithm.base.BaseTestImplclass SelectionSort : BaseTestImpl() {    override fun sort(a: Array<Comparable<*>>) {        val N = a.size        var min: Int        for (i in 0 until N) {            min = i            for (j in i + 1 until N) {                if (less(a[j], a[min])) min = j            }            if (min != i) {                print("before exch: ")                sop(*a)                exch(a, i, min)                print("after exch : ")                sop(*a)                println()            }        }    }}