package test.easy;import org.junit.Assert;import org.junit.Test;import algorithm.easy.BinarySearch;public class BinarySearchTest {    @Test    public void search() {        int[] arr = new int[]{-1,0,3,5,9,12};        BinarySearch bs = new BinarySearch();        Assert.assertEquals(-1, bs.search(arr, 2));        Assert.assertEquals(4, bs.search(arr, 9));        Assert.assertEquals(5, bs.search(arr, 12));    }}