package test.easy;import org.junit.Assert;import org.junit.Test;import algorithm.easy.FairCandySwap;public class FairCandySwapTest {    @Test    public void fairCandySwap() {        int[] A = new int[]{1,1};        int[] B = new int[]{2,2};        FairCandySwap swap = new FairCandySwap();        int[] res = swap.fairCandySwap(A, B);        Assert.assertEquals(1, res[0]);        Assert.assertEquals(2, res[1]);    }}