package test.easy;import org.junit.Assert;import org.junit.Test;import algorithm.easy.GameScore;public class GameScoreTest {    @Test    public void calPoints() {        GameScore score = new GameScore();        String[] ops = new String[]{"5","2","C","D","+"};        Assert.assertEquals(30, score.calPoints(ops));    }    @Test    public void calPoints2() {        GameScore score = new GameScore();        String[] ops = ops = new String[]{"5","-2","4","C","D","9","+","+"};        Assert.assertEquals(27, score.calPoints(ops));    }}