package test.easy;import org.junit.Assert;import org.junit.Test;import algorithm.easy.MaxSumAfterKNegation;public class MaxSumAfterKNegationTest {    @Test    public void largestSumAfterKNegations() {        int[] A = {4,2,3};        int k =1;        Assert.assertEquals(5, new MaxSumAfterKNegation().largestSumAfterKNegations(A,k));    }    @Test    public void largestSumAfterKNegations2() {        int[] A = {2,-3,-1,5,-4};        int k =2;        Assert.assertEquals(13, new MaxSumAfterKNegation().largestSumAfterKNegations(A,k));    }}