package test.easy;import org.junit.Assert;import org.junit.Test;import algorithm.easy.MyHashSet;public class MyHashSetTest {    @Test    public void add() {        MyHashSet hashSet = new MyHashSet();        hashSet.add(1);        hashSet.add(2);        Assert.assertTrue(hashSet.contains(1));        Assert.assertFalse(hashSet.contains(3));        hashSet.add(2);        hashSet.contains(2);        Assert.assertTrue(hashSet.contains(2));        hashSet.remove(2);        Assert.assertFalse(hashSet.contains(2));    }}