package test.easy;import org.junit.Assert;import org.junit.Test;import algorithm.easy.ShortestCompletingString;public class ShortestCompletingStringTest {    @Test    public void shortestCompletingWord() {        String p = "1s3 PSt";        String[] words= {"step","steps","stripe","stepple"};        Assert.assertEquals("steps", new ShortestCompletingString().shortestCompletingWord(p, words));    }    @Test    public void shortestCompletingWord1() {        String p = "1s3 456";        String[] words= {"looks","pest","stew","show"};        Assert.assertEquals("pest", new ShortestCompletingString().shortestCompletingWord(p, words));    }}