package test.easy;import org.junit.Assert;import org.junit.Test;import algorithm.easy.UniqueEmailAddress;public class UniqueEmailAddressTest {    @Test    public void numUniqueEmails() {        UniqueEmailAddress uniqueEmailAddress = new UniqueEmailAddress();        String[] emails = {"test.email+alex@leetcode.com","test.e.mail+bob.cathy@leetcode.com","testemail+david@lee.tcode.com"};        int count = uniqueEmailAddress.numUniqueEmails(emails);        Assert.assertEquals(count, 2);    }}